	}
})

frappe.ui.form.on('Purchase Receipt Item', {
	weight_per_bag(frm, cdt, cdn) {
		let item = locals[cdt][cdn];
		let no_of_bags = item.no_of_bags;
		if (no_of_bags) {
			frappe.model.set_value(cdt, cdn, {
				'qty': no_of_bags * item.weight_per_bag,
			});
		}
	},

	no_of_bags(frm, cdt, cdn) {
		let item = locals[cdt][cdn];
		let weight_per_bag = item.weight_per_bag;
		if (weight_per_bag) {
			frappe.model.set_value(cdt, cdn, {
				'qty': weight_per_bag * item.no_of_bags,
			});
		}
	}
})
